<script setup lang="ts">
import { useI18n } from "vue-i18n";

const { locales, setLocale, locale } = useI18n();

const switchLanguage = (code: any) => {
  setLocale(code); // This changes the language using the `setLocale` method
};

const ip = ref("");
const firstname = ref("");
const lastname = ref("");
const checkboxRef = ref(false);

onMounted(async () => {
  try {
    const response = await fetch("https://api.ipify.org?format=json");
    const data = await response.json();
    ip.value = data.ip; // ดึง IP มาแสดง
  } catch (error) {
    console.error("Error fetching IP address:", error);
  }
});

const onSubmit = async () => {
  const res = await fetch(`${useRuntimeConfig().public.apiUrl}/api/log`, {
    method: "POST",
    headers: {
      "Content-Type": "application/json",
    },
    body: JSON.stringify({
      firstname: firstname.value,
      lastname: lastname.value,
      ip_address: ip.value,
    }),
  });
  if (res?.status) {
    await fetch(`${useRuntimeConfig().public.apiUrl}/api/login`, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        ip_address: ip.value,
      }),
    });
    if (locale.value === "th") {
      navigateTo("https://www.thaiairways.com/th-th/", { external: true });
    } else if (locale.value === "en") {
      navigateTo("https://www.thaiairways.com/en-th/", { external: true });
    } else {
      navigateTo("https://www.thaiairways.com/zh-hk/", { external: true });
    }
  }
};

const handleCheckboxChange = () => {
  checkboxRef.value = !checkboxRef.value;
};
</script>
<template>
  <div
    class="max-w-sm rounded-lg overflow-hidden shadow-lg w-100"
    style="background-color: #00000066"
  >
    <!-- Image Section -->
    <div class="flex items-center justify-center" style="padding-top: 16px">
      <svg
        width="204"
        height="60"
        viewBox="0 0 204 60"
        fill="none"
        xmlns="http://www.w3.org/2000/svg"
        xmlns:xlink="http://www.w3.org/1999/xlink"
      >
        <rect x="0.5" width="203" height="60" fill="url(#pattern0_84_15)" />
        <defs>
          <pattern
            id="pattern0_84_15"
            patternContentUnits="objectBoundingBox"
            width="1"
            height="1"
          >
            <use
              xlink:href="#image0_84_15"
              transform="matrix(0.0029264 0 0 0.00990099 -0.000414573 0)"
            />
          </pattern>
          <image
            id="image0_84_15"
            width="342"
            height="101"
            preserveAspectRatio="none"
            xlink:href="data:image/png;base64,"
          />
        </defs>
      </svg>
    </div>
    <div class="flex items-center justify-center" style="padding-top: 16px">
      <svg
        width="204"
        height="31"
        viewBox="0 0 204 31"
        fill="none"
        xmlns="http://www.w3.org/2000/svg"
        xmlns:xlink="http://www.w3.org/1999/xlink"
      >
        <rect x="0.5" width="203" height="31" fill="url(#pattern0_84_16)" />
        <defs>
          <pattern
            id="pattern0_84_16"
            patternContentUnits="objectBoundingBox"
            width="1"
            height="1"
          >
            <use
              xlink:href="#image0_84_16"
              transform="matrix(0.000657462 0 0 0.00430532 0 -0.0080274)"
            />
          </pattern>
          <image
            id="image0_84_16"
            width="1521"
            height="236"
            preserveAspectRatio="none"
            xlink:href="data:image/png;base64,"
          />
        </defs>
      </svg>
    </div>
    <div class="flex justify-center items-center space-x-2 mt-5">
      <button
        v-for="localeItem in locales"
        :key="localeItem.code"
        @click="switchLanguage(localeItem.code)"
      >
        <!-- Custom flag (SVG or image) -->
        <div v-html="localeItem.icon"></div>
        <!-- Language Name -->
        <!-- <span>{{ localeItem.name }}</span> -->
      </button>
    </div>
    <div class="flex justify-center items-center space-x-2 mt-5">
      <span class="text-white">{{ $t("welcome") }}</span>
    </div>
    <!-- Card Content -->
    <div class="p-6">
      <div class="max-w-xs mx-auto">
        <!-- Text Input -->
        <!-- <label for="username" class="block text-sm font-medium text-white">{{
          $t("usernameInput")
        }}</label> -->
        <input
          v-model="firstname"
          type="text"
          id="username"
          name="username"
          class="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm text-white"
          style="background-color: #00000080"
          :placeholder="$t('firstname')"
        />

        <!-- Password Input -->
        <!-- <label
          for="password"
          class="block text-sm font-medium text-white mt-4"
          >{{ $t("passwordInput") }}</label
        > -->
        <input
          v-model="lastname"
          type="text"
          id="password"
          name="password"
          class="mt-5 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm text-white"
          style="background-color: #00000080"
          :placeholder="$t('lastname')"
        />
      </div>
      <label class="flex justify-center mt-5">
        <input
          type="checkbox"
          class="form-checkbox text-blue-600 border-gray-300 rounded"
          :checked="checkboxRef"
          @change="handleCheckboxChange"
        />
        <!-- <span class="ml-2 text-white">{{ $t("confirmCheckbox") }}</span> -->
        <a
          :href="
            locale === 'th'
              ? 'https://www.thaiairways.com/th-th/content/privacy-notice/'
              : locale === 'en'
              ? 'https://www.thaiairways.com/en-th/content/privacy-notice/'
              : 'https://www.thaiairways.com/zh-cn/content/privacy-notice/'
          "
          target="_blank"
          class="text-white ms-3"
          style="font-size: 12px; cursor: pointer"
          >{{ $t("privacyNotice") }}</a
        >
      </label>
      <!-- Action Button -->
      <div class="mt-4 flex justify-center">
        <button
          :disabled="checkboxRef === false || !firstname || !lastname"
          @click="onSubmit"
          class="px-4 py-2 w-100 bg-yellow-500 text-black rounded-md hover:bg-yellow-600 focus:outline-none focus:ring-2 focus:ring-yellow-400"
        >
          {{ $t("action_button") }}
        </button>
      </div>
    </div>
  </div>
</template>

